import React, { Component } from 'react'
import PropTypes from 'prop-types'
import 'isomorphic-unfetch';
import axios from 'axios';

export class POSTREQ extends Component {
    constructor(props:any) {
        super(props)
    
        this.state = {
            userId: '',
            title: '',
            Body:'',
             
        }
    }

    changeHandler = (e) =>{
        this.setState({[e.target.name] : e.target.value })
    }

    suubmitHandler = e =>{
        e.preventDefault()
        console.log(this.state)
        axios.post('/api/user' , this.state )

    }
    

    render() {
        const  {userId , title , Body} = this.state
        return (
            <div>
                <form onSubmit={this.suubmitHandler}>
                 <input type="text" placeholder="Name" name="userId"  value={userId} onChange={this.changeHandler} />
                <input type="text" placeholder="Mobile" name="title" value={title} onChange={this.changeHandler} />
                <input type="text" placeholder="Message" name="Body" value={Body} onChange={this.changeHandler} />
                <button type="submit">submit</button>
                </form>
            </div>
        )
    }
}

export default POSTREQ
